<?php

	function a(){
		$sum  = 0;
		return function($i) use (&$sum){
			$sum += $i;
			return $sum;
		};
	}

	$a = a();

	print_r($a);

	echo $a(1),"\n";
	print_r($a);

	echo $a(1);
```

